import React, { useState, useEffect } from "react";
import { Doughnut, Line } from "react-chartjs-2";
import {
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";
import axios from "axios";
import Chart from "chart.js/auto";

Chart.register(
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

export default function Charts() {
  const [severityData, setSeverityData] = useState({
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: ["#ff3333", "#ffb347", "#ffe066", "#6fd3c3"], // red, orange, yellow, teal
      },
    ],
  });

  const [trendsData, setTrendsData] = useState({
    labels: [],
    datasets: [
      {
        label: "New Vulnerabilities",
        data: [],
        backgroundColor: "rgba(97, 218, 251, 0.15)", // theme blue, lighter
        borderColor: "#61dafb", // theme blue
        fill: true,
        tension: 0.3,
      },
    ],
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const [severityResponse, trendsResponse] = await Promise.all([
          axios.get("/api/vulnerabilities/charts/severity"),
          axios.get("/api/vulnerabilities/charts/trends"),
        ]);

        // Update severity chart
        setSeverityData({
          labels: severityResponse.data.labels,
          datasets: [
            {
              data: severityResponse.data.data,
              backgroundColor: ["#ff3333", "#ffb347", "#ffe066", "#6fd3c3"],
            },
          ],
        });

        // Update trends chart
        setTrendsData({
          labels: trendsResponse.data.labels,
          datasets: [
            {
              label: "New Vulnerabilities",
              data: trendsResponse.data.data,
              backgroundColor: "rgba(97, 218, 251, 0.15)",
              borderColor: "#61dafb",
              fill: true,
              tension: 0.3,
            },
          ],
        });
      } catch (error) {
        console.error("Error fetching chart data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchChartData();
  }, []);

  return (
    <>
      <style>{`
        .charts-section {
          max-width: 1200px;
          margin: 0 auto 2rem;
          display: flex;
          gap: 2rem;
          flex-wrap: wrap;
          padding: 0 1rem;
        }
        .chart-container {
          flex: 1 1 480px;
          background-color: var(--card-bg, #fff);
          border-radius: 14px;
          box-shadow: var(--shadow-md, 0 6px 18px #61dafb22);
          padding: 1.5rem 1.8rem;
          transition: background-color 0.4s ease;
        }
        .card__header {
          border-bottom: 2px solid #61dafb;
          padding-bottom: 0.5rem;
          margin-bottom: 1rem;
          font-weight: 700;
          font-size: 1.3rem;
          color: var(--text-primary, #282c34);
          transition: color 0.4s ease;
        }
        body:not(.light-theme) .card__header {
          color: #cbd3da;
          border-color: #1fb8cdbb;
        }
        .card__body {
          height: 300px;
        }
        .charts-section.loading {
          font-size: 1.2rem;
          text-align: center;
          color: var(--text-secondary, #777);
          width: 100%;
          padding: 3rem;
        }
        @media (max-width: 600px) {
          .charts-section {
            flex-direction: column;
            padding: 0 0.5rem;
          }
          .chart-container {
            flex: 1 1 100%;
          }
          .card__body {
            height: 270px;
          }
        }
      `}</style>
      {loading ? (
        <div className="charts-section loading">Loading charts...</div>
      ) : (
        <section className="charts-section" aria-label="Vulnerability charts">
          <div className="chart-container">
            <div className="card__header">Severity Distribution</div>
            <div className="card__body">
              <Doughnut data={severityData} aria-label="Severity distribution doughnut chart" role="img" />
            </div>
          </div>
          <div className="chart-container">
            <div className="card__header">Vulnerability Trends</div>
            <div className="card__body">
              <Line data={trendsData} aria-label="Vulnerability trends line chart" role="img" />
            </div>
          </div>
        </section>
      )}
    </>
  );
}
