import React from 'react';

function VulnerabilityModal({ vuln, onClose }) {
  if (!vuln) return null;
  return (
    <div style={{
      position:'fixed',top:0,left:0,right:0,bottom:0,
      background:'rgba(0,0,0,0.6)',display:'flex',alignItems:'center',justifyContent:'center',zIndex:99
    }}>
      <div style={{background:'#fff',padding:24,minWidth:350,position:'relative'}}>
        <button onClick={onClose} style={{position:'absolute',top:4,right:8}}>X</button>
        <h2>{vuln.title}</h2>
        <div><strong>CVE:</strong> {vuln.cve}</div>
        <div><strong>Description:</strong> {vuln.description}</div>
        <div><strong>Severity:</strong> {vuln.severity} ({vuln.cvss})</div>
        <div><strong>Vendor/Product:</strong> {vuln.vendor} {vuln.product}</div>
        <div><strong>Status:</strong> {vuln.status}</div>
        <div><strong>Published:</strong> {new Date(vuln.published).toLocaleDateString()}</div>
        {vuln.references && <div>
          <strong>References:</strong>
          <ul>
            {vuln.references.map(ref=>
              <li key={ref}><a href={ref} target="_blank" rel="noreferrer">{ref}</a></li>
            )}
          </ul>
        </div>}
        {vuln.impact && <div>
          <div><strong>Confidentiality:</strong> {vuln.impact.confidentiality}</div>
          <div><strong>Integrity:</strong> {vuln.impact.integrity}</div>
          <div><strong>Availability:</strong> {vuln.impact.availability}</div>
        </div>}
      </div>
    </div>
  );
}
export default VulnerabilityModal;
