// eslint-disable-next-line no-unused-vars
import React, { useState, useEffect } from 'react';
import { Doughnut, Line } from 'react-chartjs-2';
import { Chart, ArcElement, CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend } from 'chart.js';

Chart.register(ArcElement, CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

const sidebarItems = [
  { icon: 'fa-tachometer-alt', label: 'Dashboard' },
  { icon: 'fa-bug', label: 'Vulnerabilities' },
  { icon: 'fa-cog', label: 'Settings' },
  { icon: 'fa-chart-bar', label: 'Reports' }
];

const sampleVulns = [
  {
    cve: 'CVE-2024-1234',
    title: 'Cisco IOS XE Web UI Privilege Escalation Vulnerability',
    vendor: 'Cisco',
    severity: 'CRITICAL',
    cvss: 9.8,
    status: 'Active',
    published: '2024-10-05'
  },
  {
    cve: 'CVE-2024-5678',
    title: 'Juniper Networks Junos OS Command Injection',
    vendor: 'Juniper',
    severity: 'HIGH',
    cvss: 8.1,
    status: 'Under Investigation',
    published: '2024-10-03'
  },
  {
    cve: 'CVE-2024-9012',
    title: 'Honeywell Experion PKS Buffer Overflow',
    vendor: 'Honeywell',
    severity: 'MEDIUM',
    cvss: 5.3,
    status: 'Resolved',
    published: '2024-09-28'
  }
];

const metricDefinitions = [
  { icon: 'fa-shield-alt', label: 'Total Vulnerabilities', key: 'total' },
  { icon: 'fa-exclamation-circle', label: 'Critical/High', key: 'critical' },
  { icon: 'fa-fire', label: 'Active Threats', key: 'active' },
  { icon: 'fa-check-circle', label: 'Resolved', key: 'resolved' }
];

const Dashboard = () => {
  // eslint-disable-next-line no-unused-vars
  const [vulnerabilities, setVulnerabilities] = useState(sampleVulns);
  const total = vulnerabilities.length;
  const critical = vulnerabilities.filter(v => v.severity === 'CRITICAL' || v.severity === 'HIGH').length;
  const active = vulnerabilities.filter(v => v.status === 'Active').length;
  const resolved = vulnerabilities.filter(v => v.status === 'Resolved').length;

  const metricValues = { total, critical, active, resolved };

  // Prepare charts data
  const severityCounts = ['CRITICAL', 'HIGH', 'MEDIUM', 'LOW'].map(sev =>
    vulnerabilities.filter(v => v.severity === sev).length
  );
  const donutColors = ['#00bcd4', '#f9c851', '#d9534f', '#e3e1d1'];
  const donutLabels = ['Critical', 'High', 'Medium', 'Low'];

  const donutData = {
    labels: donutLabels,
    datasets: [{
      data: severityCounts,
      backgroundColor: donutColors,
      borderWidth: 1
    }]
  };

  const lineLabels = vulnerabilities.map(v => v.published);
  const lineData = {
    labels: lineLabels,
    datasets: [{
      label: 'New Vulnerabilities',
      data: Array(lineLabels.length).fill(1),
      borderColor: '#00bcd4',
      backgroundColor: 'rgba(0, 188, 212, 0.15)',
      fill: true,
      tension: 0.2
    }]
  };

  const now = new Date();
  const dateStr = now.toLocaleString('en-US', {
    weekday: 'long', year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit'
  });

  return (
    <div className="app-container">
      <nav className="sidebar">
        <div className="sidebar-header">
          <div className="logo"><i className="fas fa-shield-virus"></i> <span>VulnScraper</span></div>
          <button className="theme-toggle"><i className="fas fa-moon"></i></button>
        </div>
        <div className="nav-menu">
          {sidebarItems.map(item => (
            // eslint-disable-next-line jsx-a11y/anchor-is-valid
            <a key={item.label} className="nav-item active" href="#"><i className={`fas ${item.icon}`}></i> <span>{item.label}</span></a>
          ))}
        </div>
      </nav>

      <main className="main-content">
        <header className="header">
          <div className="header-left">
            <h1 className="page-title">Dashboard</h1>
          </div>
          <div className="header-right">
            <div className="current-time">{dateStr}</div>
            <div className="user-profile"><i className="fas fa-user-circle"></i> Admin</div>
          </div>
        </header>

        <div className="alerts-section">
          <div className="alert alert-critical">
            <i className="fas fa-exclamation-triangle"></i>
            <span>New Critical vulnerability detected: Cisco IOS XE Web UI Privilege Escalation</span>
            <button className="alert-close">&times;</button>
          </div>
        </div>

        <div className="metrics-grid">
          {metricDefinitions.map(def => (
            <div key={def.label} className={`metric-card${def.key === 'critical' ? ' critical' : def.key === 'active' ? ' active' : def.key === 'resolved' ? ' resolved' : ''}`}>
              <div className="metric-icon">
                <i className={`fas ${def.icon}`}></i>
              </div>
              <div className="metric-content">
                <div className="metric-number">{metricValues[def.key]}</div>
                <div className="metric-label">{def.label}</div>
              </div>
            </div>
          ))}
        </div>

        <div className="charts-section">
          <div className="chart-container">
            <div className="card">
              <div className="card__header"><h3>Severity Distribution</h3></div>
              <div className="card__body"><Doughnut data={donutData} /></div>
            </div>
          </div>
          <div className="chart-container">
            <div className="card">
              <div className="card__header"><h3>Vulnerability Trends</h3></div>
              <div className="card__body"><Line data={lineData} /></div>
            </div>
          </div>
        </div>

        <div className="recent-vulnerabilities">
          <div className="card">
            <div className="card__header"><h3>Recent Vulnerabilities</h3></div>
            <div className="card__body">
              <table className="vulnerabilities-table">
                <thead>
                  <tr>
                    <th>CVE</th>
                    <th>Title</th>
                    <th>Vendor</th>
                    <th>Severity</th>
                    <th>CVSS</th>
                    <th>Status</th>
                    <th>Published</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {vulnerabilities.map(vuln => (
                    <tr key={vuln.cve}>
                      <td><span className="cve-code">{vuln.cve}</span></td>
                      <td>{vuln.title.length > 40 ? vuln.title.slice(0, 38) + '...' : vuln.title}</td>
                      <td>{vuln.vendor}</td>
                      <td><span className={`pill ${vuln.severity.toLowerCase()}`}>{vuln.severity}</span></td>
                      <td><b>{vuln.cvss}</b></td>
                      <td><span className={`pill ${vuln.status.replace(/\s/g, '-').toLowerCase()}`}>{vuln.status}</span></td>
                      <td>{(new Date(vuln.published)).toLocaleDateString("en-US", { month: 'short', day: 'numeric', year: 'numeric' })}</td>
                      <td><button className="action-btn"><i className="fas fa-eye"></i></button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

      </main>
    </div>
  );
};

export default Dashboard;
