import React, { useState } from "react";
import Metrics from "./Metrics";
import Charts from "./Charts";
import RecentVulns from "./RecentVulns";

export default function Dashboard() {
  const [showAlert, setShowAlert] = useState(true);

  return (
    <>
      <style>{`
        /* Container centering and spacing */
        .alerts-section {
          margin: 2rem auto 2rem;
          max-width: 1200px;
          padding: 0 1rem;
        }

        /* Alert box styling */
        .alert {
          position: relative;
          border-radius: 12px;
          padding: 1rem 2rem 1rem 1.8rem;
          font-weight: 600;
          font-size: 1rem;
          display: flex;
          align-items: center;
          gap: 1rem;
          background-color: #e53e3e;
          color: white;
          box-shadow: 0 4px 15px rgba(229, 62, 62, 0.4);
          user-select: none;
        }

        .alert-critical i {
          font-size: 1.6rem;
          flex-shrink: 0;
        }

        /* Close button styling */
        .alert-close {
          position: absolute;
          top: 50%;
          right: 0.8rem;
          transform: translateY(-50%);
          background: transparent;
          border: none;
          font-size: 1.4rem;
          color: white;
          cursor: pointer;
          line-height: 1;
          font-weight: 900;
          user-select: none;
          transition: color 0.3s ease;
          padding: 0;
        }

        .alert-close:hover {
          color: #fff7f7;
        }

        /* Dashboard grid */
        .dashboard-view {
          max-width: 1200px;
          margin: 0 auto 2rem auto;
          padding: 0 2rem;
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
          gap: 2.5rem;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        /* Responsive small screens */
        @media (max-width: 640px) {
          .dashboard-view {
            grid-template-columns: 1fr;
            padding: 0 1rem 1.5rem;
          }
        }
      `}</style>

      <div className="alerts-section" id="alertsSection">
        {showAlert && (
          <div className="alert alert-critical" role="alert" aria-live="assertive">
            <i className="fas fa-exclamation-triangle" aria-hidden="true"></i>
            <span>New Critical vulnerability detected: Cisco IOS XE Web UI Privilege Escalation</span>
            <button
              aria-label="Close alert"
              className="alert-close"
              onClick={() => setShowAlert(false)}
            >
              &times;
            </button>
          </div>
        )}
      </div>

      <div className="dashboard-view" id="dashboardView">
        <Metrics />
        <Charts />
        <RecentVulns />
      </div>
    </>
  );
}
