import React from "react";
import { Box, Container, Grid, Card, CardContent, Typography, IconButton, Alert } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import Header from "./Header";
import Metrics from "./Metrics";
import Charts from "./Charts";
import RecentVulns from "./RecentVulns";

export default function Dashboard() {
  const [alertVisible, setAlertVisible] = React.useState(true);

  return (
    <>
      <Header />
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        {alertVisible && (
          <Alert
            severity="error"
            action={
              <IconButton
                aria-label="close"
                color="inherit"
                size="small"
                onClick={() => setAlertVisible(false)}
              >
                <CloseIcon fontSize="inherit" />
              </IconButton>
            }
            sx={{ mb: 3, fontWeight: "bold" }}
          >
            New Critical vulnerability detected: Cisco IOS XE Web UI Privilege Escalation
          </Alert>
        )}

        <Grid container spacing={3}>
          {/* Metrics Section */}
          <Grid item xs={12}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Metrics Overview
                </Typography>
                <Metrics />
              </CardContent>
            </Card>
          </Grid>

          {/* Charts Section */}
          <Grid item xs={12} md={8}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Vulnerability Charts
                </Typography>
                <Charts />
              </CardContent>
            </Card>
          </Grid>

          {/* Recent Vulnerabilities Section */}
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Recent Vulnerabilities
                </Typography>
                <RecentVulns />
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Container>
    </>
  );
}
