import React from "react";

export default function Header() {
  // Format the date like "Sunday, October 12, 2025 at 4:14 PM"
  const now = new Date().toLocaleString('en-US', { dateStyle: 'full', timeStyle: 'short' });

  return (
    <header
      className="header"
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        background: "#fff",
        padding: "20px 32px 8px 32px",
        borderBottom: "1px solid #e3e6ea",
        fontFamily: "inherit",
        minHeight: "60px"
      }}
    >
      <div
        className="header-left"
        style={{
          fontSize: "2rem",
          fontWeight: "bold",
          color: "#22334c",
        }}
      >
        Dashboard
      </div>
      <div
        className="header-right"
        style={{
          display: "flex",
          alignItems: "center",
          gap: "28px",
        }}
      >
        <div
          className="current-time"
          style={{
            color: "#343a40",
            fontSize: "1rem"
          }}
        >
          {now}
        </div>
        <div
          className="user-profile"
          style={{
            display: "flex",
            alignItems: "center",
            color: "#00687d",
            fontWeight: "bold",
            fontSize: "1rem"
          }}
        >
          <i
            className="fas fa-user-circle"
            style={{ fontSize: "1.3rem", marginRight: "6px" }}
          ></i>
          <span>Admin</span>
        </div>
      </div>
    </header>
  );
}
