import React, { useState, useEffect } from "react";
import { Doughnut, Line } from "react-chartjs-2";
import {
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";
import axios from "axios";
import Chart from "chart.js/auto";
import "./dashboard.css";

Chart.register(
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

export default function Charts() {
  const [severityData, setSeverityData] = useState({
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: ["#ff3333", "#ffb347", "#ffe066", "#6fd3c3"],
      },
    ],
  });
  const [trendsData, setTrendsData] = useState({
    labels: [],
    datasets: [
      {
        label: "New Vulnerabilities",
        data: [],
        backgroundColor: "rgba(97, 218, 251, 0.15)",
        borderColor: "#61dafb",
        fill: true,
        tension: 0.3,
      },
    ],
  });
  const [loading, setLoading] = useState(true);

  // Dark or light mode detection
  const [isDarkTheme, setIsDarkTheme] = useState(
    document.body.classList.contains("light-theme") === false
  );
  useEffect(() => {
    // Listen for theme changes (optional advanced, or set only on mount)
    const observer = new MutationObserver(() => {
      setIsDarkTheme(document.body.classList.contains("light-theme") === false);
    });
    observer.observe(document.body, { attributes: true, attributeFilter: ["class"] });
    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const [severityResponse, trendsResponse] = await Promise.all([
          axios.get("/api/vulnerabilities/charts/severity"),
          axios.get("/api/vulnerabilities/charts/trends"),
        ]);
        setSeverityData({
          labels: severityResponse.data.labels,
          datasets: [
            {
              data: severityResponse.data.data,
              backgroundColor: ["#ff3333", "#ffb347", "#ffe066", "#6fd3c3"],
            },
          ],
        });
        setTrendsData({
          labels: trendsResponse.data.labels,
          datasets: [
            {
              label: "New Vulnerabilities",
              data: trendsResponse.data.data,
              backgroundColor: "rgba(97, 218, 251, 0.15)",
              borderColor: "#61dafb",
              fill: true,
              tension: 0.3,
            },
          ],
        });
      } catch (error) {
        console.error("Error fetching chart data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchChartData();
  }, []);

  // Chart.js options with dark/light colors.
  const getChartOptions = () => ({
    responsive: true,
    plugins: {
      legend: {
        labels: {
          color: isDarkTheme ? "#b2c7d2" : "#282c34",
        },
      },
      tooltip: {
        backgroundColor: isDarkTheme ? "#23282f" : "#fff",
        titleColor: isDarkTheme ? "#61dafb" : "#000",
        bodyColor: isDarkTheme ? "#cbd3da" : "#000",
      },
    },
    scales: {
      x: {
        ticks: { color: isDarkTheme ? "#b2c7d2" : "#282c34" },
        grid: { color: isDarkTheme ? "#444" : "#eee" },
      },
      y: {
        ticks: { color: isDarkTheme ? "#b2c7d2" : "#282c34" },
        grid: { color: isDarkTheme ? "#444" : "#eee" },
      },
    },
  });

  return loading ? (
    <div className="loading">Loading charts...</div>
  ) : (
    <>
      <div className="dashboard-card">
        <div className="dashboard-card-accent"></div>
        <div className="dashboard-chart-title">Severity Distribution</div>
        <div className="dashboard-chart">
          <Doughnut data={severityData} options={getChartOptions()} />
        </div>
      </div>
      <div className="dashboard-card">
        <div className="dashboard-card-accent"></div>
        <div className="dashboard-chart-title">Vulnerability Trends</div>
        <div className="dashboard-chart">
          <Line data={trendsData} options={getChartOptions()} />
        </div>
      </div>
    </>
  );
}
