import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "./signup.css"; // Import the signup CSS

const Signup = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/auth/signup', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });
    const data = await response.json();
    if (response.ok) {
      setMessage('Signup successful! Please login.');
      setTimeout(() => navigate('/login'), 2000); // Redirect after 2 seconds
    } else {
      setMessage(data.error || 'Signup failed.');
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-header">
        <i className="fas fa-shield-virus signup-logo" aria-hidden="true"></i>
        <h1 className="signup-title">YourAppName</h1>
      </div>
      <form className="signup-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit" className="signup-btn">Signup</button>
      </form>
      {message && <p className="signup-message">{message}</p>}
      <p className="signup-login-link">
        Already have an account?{' '}
        <button onClick={() => navigate('/login')} className="login-btn-link">Login</button>
      </p>
    </div>
  );
};

export default Signup;
