import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { SeverityChart, TrendsChart } from './Charts';

const Dashboard = () => {
  const [vulnerabilities, setVulnerabilities] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/vulnerabilities')
      .then(res => setVulnerabilities(res.data))
      .catch(err => console.error(err));
  }, []);

  const totalVulns = vulnerabilities.length;
  const criticalCount = vulnerabilities.filter(v => v.severity === 'Critical' || v.severity === 'High').length;
  const activeThreats = vulnerabilities.filter(v => v.status === 'Active').length;
  const resolvedCount = vulnerabilities.filter(v => v.status === 'Resolved').length;

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial, sans-serif' }}>
      <h1>Dashboard</h1>

      <div style={{ display: 'flex', gap: '1rem', marginBottom: '2rem' }}>
        <MetricCard icon="fas fa-shield-alt" label="Total Vulnerabilities" number={totalVulns} />
        <MetricCard icon="fas fa-exclamation-circle" label="Critical/High" number={criticalCount} critical />
        <MetricCard icon="fas fa-fire" label="Active Threats" number={activeThreats} active />
        <MetricCard icon="fas fa-check-circle" label="Resolved" number={resolvedCount} resolved />
      </div>

      <div style={{ display: 'flex', gap: '2rem' }}>
        <div style={{ flex: 1 }}>
          <h3>Severity Distribution</h3>
          <SeverityChart vulnerabilities={vulnerabilities} />
        </div>
        <div style={{ flex: 1 }}>
          <h3>Vulnerability Trends</h3>
          <TrendsChart vulnerabilities={vulnerabilities} />
        </div>
      </div>

      <RecentVulnerabilities vulnerabilities={vulnerabilities} />
    </div>
  );
};

const MetricCard = ({ icon, label, number, critical, active, resolved }) => {
  let color = '#555';
  if (critical) color = '#d9534f';
  if (active) color = '#f0ad4e';
  if (resolved) color = '#5cb85c';

  return (
    <div style={{
      flex: 1,
      backgroundColor: '#f7f7f7',
      padding: '1rem',
      borderRadius: '8px',
      textAlign: 'center',
      boxShadow: '0 0 5px rgba(0,0,0,0.1)'
    }}>
      <i className={icon} style={{ fontSize: '2rem', color }}></i>
      <h2 style={{ margin: '0.5rem 0' }}>{number}</h2>
      <p style={{ color: '#333' }}>{label}</p>
    </div>
  );
};

const RecentVulnerabilities = ({ vulnerabilities }) => (
  <div style={{ marginTop: '3rem' }}>
    <h2>Recent Vulnerabilities</h2>
    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
      <thead>
        <tr>
          <th style={thStyle}>CVE</th>
          <th style={thStyle}>Title</th>
          <th style={thStyle}>Vendor</th>
          <th style={thStyle}>Severity</th>
          <th style={thStyle}>CVSS</th>
          <th style={thStyle}>Status</th>
          <th style={thStyle}>Published</th>
        </tr>
      </thead>
      <tbody>
        {vulnerabilities.map(vuln => (
          <tr key={vuln.cve}>
            <td style={tdStyle}>{vuln.cve}</td>
            <td style={tdStyle}>{vuln.title}</td>
            <td style={tdStyle}>{vuln.vendor}</td>
            <td style={tdStyle}>{vuln.severity}</td>
            <td style={tdStyle}>{vuln.cvss}</td>
            <td style={tdStyle}>{vuln.status}</td>
            <td style={tdStyle}>{new Date(vuln.published).toLocaleDateString()}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const thStyle = {
  borderBottom: '2px solid #ddd',
  padding: '0.5rem',
  textAlign: 'left',
};
const tdStyle = {
  borderBottom: '1px solid #eee',
  padding: '0.5rem',
};

export default Dashboard;
