import React, { useState, useEffect } from "react";
import axios from "axios";
import "./dashboard.css"; // (Make sure this is imported)

export default function Metrics() {
  const [metrics, setMetrics] = useState({
    totalVulns: 0,
    criticalCount: 0,
    activeThreats: 0,
    resolvedCount: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const response = await axios.get("/api/vulnerabilities/metrics");
        setMetrics(response.data);
      } catch (error) {
        console.error("Error fetching metrics:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchMetrics();
  }, []);

  const displayCount = (count) =>
    count === undefined || count === null ? "0" : count.toLocaleString();

  return loading ? (
    <div className="loading">Loading metrics...</div>
  ) : (
    <>
      <div className="dashboard-card">
        <div className="dashboard-card-accent"></div>
        <span className="dashboard-card-icon">
          <i className="fas fa-shield-alt"></i>
        </span>
        <div className="dashboard-metric-num">{displayCount(metrics.totalVulns)}</div>
        <div className="dashboard-metric-label">Total Vulnerabilities</div>
      </div>
      <div className="dashboard-card critical">
        <div className="dashboard-card-accent"></div>
        <span className="dashboard-card-icon">
          <i className="fas fa-exclamation-circle"></i>
        </span>
        <div className="dashboard-metric-num">{displayCount(metrics.criticalCount)}</div>
        <div className="dashboard-metric-label">Critical / High</div>
      </div>
      <div className="dashboard-card active">
        <div className="dashboard-card-accent"></div>
        <span className="dashboard-card-icon">
          <i className="fas fa-fire"></i>
        </span>
        <div className="dashboard-metric-num">{displayCount(metrics.activeThreats)}</div>
        <div className="dashboard-metric-label">Active Threats</div>
      </div>
      <div className="dashboard-card resolved">
        <div className="dashboard-card-accent"></div>
        <span className="dashboard-card-icon">
          <i className="fas fa-check-circle"></i>
        </span>
        <div className="dashboard-metric-num">{displayCount(metrics.resolvedCount)}</div>
        <div className="dashboard-metric-label">Resolved</div>
      </div>
    </>
  );
}
