import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  // State to hold form inputs: name, email, password
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  // State to hold feedback message (success or error)
  const [message, setMessage] = useState('');

  // React Router's navigate hook to programmatically change routes
  const navigate = useNavigate();

  // Updates the formData state when input values change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handles form submission asynchronously
  const handleSubmit = async (e) => {
    e.preventDefault();  // Prevent default form submit page reload

    // Send POST request to signup API endpoint with form data as JSON
    const response = await fetch('http://localhost:5000/api/auth/signup', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });

    // Parse the JSON response from backend
    const data = await response.json();

    if (response.ok) {
      // If signup successful, show success message
      setMessage('Signup successful! Please login.');

      // Redirect to login page after 2 seconds delay
      setTimeout(() => navigate('/login'), 2000);
    } else {
      // If signup failed, show error message from backend or generic message
      setMessage(data.error || 'Signup failed.');
    }
  };

  return (
    <div>
      <h2>Signup</h2>
      <form onSubmit={handleSubmit}>
        {/* Name input with change handler */}
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <br />

        {/* Email input with change handler */}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <br />

        {/* Password input with change handler */}
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <br />

        {/* Submit button */}
        <button type="submit">Signup</button>
      </form>

      {/* Display message if any */}
      {message && <p>{message}</p>}

      {/* Link to login page with button */}
      <p>
        Already have an account?{' '}
        <button onClick={() => navigate('/login')}>Login</button>
      </p>
    </div>
  );
};

export default Signup;
