import React, { useState } from 'react';
import { vulnerabilityData } from './data';
import SeverityChart from './SeverityChart';
import TrendsChart from './TrendsChart';
import VulnerabilitiesTable from './VulnerabilitiesTable';
import VulnerabilityModal from './VulnerabilityModal';

function Dashboard() {
  const [modalVuln, setModalVuln] = useState(null);
  const stats = vulnerabilityData.statistics;

  return (
    <div style={{padding:16}}>
      <h1>VulnScraper Dashboard</h1>
      {/* Metric Cards */}
      <div style={{display:'flex',gap:24,marginBottom:20}}>
        <div>Total Vulnerabilities: <b>{stats.total}</b></div>
        <div>Critical/High: <b>{stats.critical + stats.high}</b></div>
        <div>Active Threats: <b>{stats.active}</b></div>
        <div>Resolved: <b>{stats.resolved}</b></div>
      </div>

      {/* Charts */}
      <div style={{display:'flex',gap:40,marginBottom:32}}>
        <div>
          <h3>Severity Distribution</h3>
          <SeverityChart statistics={stats} />
        </div>
        <div>
          <h3>Vulnerability Trends</h3>
          <TrendsChart trends={vulnerabilityData.trends} />
        </div>
      </div>

      {/* Table */}
      <h3>Vulnerabilities</h3>
      <VulnerabilitiesTable
        vulnerabilities={vulnerabilityData.vulnerabilities}
        onViewDetails={setModalVuln}
      />
      <VulnerabilityModal vuln={modalVuln} onClose={() => setModalVuln(null)} />
    </div>
  );
}
export default Dashboard;
