import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Metrics() {
  const [metrics, setMetrics] = useState({
    totalVulns: 0,
    criticalCount: 0,
    activeThreats: 0,
    resolvedCount: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const response = await axios.get("/api/vulnerabilities/metrics");
        setMetrics(response.data);
      } catch (error) {
        console.error("Error fetching metrics:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchMetrics();
  }, []);

  // Helper to display number or "0" explicitly
  const displayCount = (count) => {
    if (count === undefined || count === null) return "0";
    return count.toLocaleString();
  };

  return (
    <>
      <style>{`
        .metrics-grid {
          display: flex;
          flex-wrap: wrap;
          gap: 1.6rem;
          justify-content: space-between;
          max-width: 100%;
          margin: 0 auto;
          color: var(--text-primary, #282c34);
          transition: color 0.3s ease;
        }

        body.light-theme .metrics-grid {
          color: var(--text-primary, #282c34);
        }

        body:not(.light-theme) .metrics-grid {
          color: #cbd3da;
        }

        .metric-card {
          background: var(--card-bg, #fff);
          border-radius: 14px;
          box-shadow: var(--shadow-md, 0 6px 18px #61dafb22);
          padding: 1.5rem 2rem;
          flex: 1 1 200px;
          display: flex;
          align-items: center;
          gap: 1.25rem;
          min-width: 180px;
          transition: box-shadow 0.25s ease, transform 0.25s ease, background-color 0.4s ease;
          cursor: default;
          user-select: none;
        }

        body:not(.light-theme) .metric-card {
          background: #23282f;
          box-shadow: 0 6px 18px #1fb8cd33;
        }

        .metric-card:hover {
          box-shadow: var(--shadow-lg, 0 10px 30px #61dafb40);
          transform: translateY(-6px) scale(1.02);
        }

        .metric-icon {
          font-size: 2rem;
          flex-shrink: 0;
          color: var(--accent, #61dafb);
          transition: color 0.4s ease;
        }

        .metric-card.critical .metric-icon {
          color: var(--color-critical, #e53e3e);
        }
        .metric-card.active .metric-icon {
          color: var(--color-warning, #f6ad55);
        }
        .metric-card.resolved .metric-icon {
          color: var(--color-success, #38a169);
        }

        .metric-content {
          display: flex;
          flex-direction: column;
        }

        .metric-number {
          font-size: 2.25rem;
          font-weight: 900;
          line-height: 1;
          margin-bottom: 0.2rem;
          font-feature-settings: 'tnum';
          font-variant-numeric: tabular-nums;
          color: inherit; /* inherit theme color */
        }

        .metric-label {
          font-weight: 600;
          font-size: 1rem;
          color: var(--text-secondary, #555);
          user-select: none;
          transition: color 0.4s ease;
        }

        body:not(.light-theme) .metric-label {
          color: #a0a8ad;
        }

        .metrics-grid.loading {
          font-size: 1.4rem;
          font-weight: 600;
          color: var(--text-secondary, #777);
          justify-content: center;
          padding: 2rem 1rem;
        }

        @media (max-width: 600px) {
          .metrics-grid {
            flex-direction: column;
            gap: 2rem;
          }
        }
      `}</style>

      {loading ? (
        <div className="metrics-grid loading">Loading metrics...</div>
      ) : (
        <div className="metrics-grid" role="region" aria-label="Vulnerability Metrics">
          <div className="metric-card">
            <div className="metric-icon" aria-hidden="true">
              <i className="fas fa-shield-alt"></i>
            </div>
            <div className="metric-content">
              <div className="metric-number" id="totalVulns" aria-live="polite" aria-atomic="true">
                {displayCount(metrics.totalVulns)}
              </div>
              <div className="metric-label">Total Vulnerabilities</div>
            </div>
          </div>

          <div className="metric-card critical">
            <div className="metric-icon" aria-hidden="true">
              <i className="fas fa-exclamation-circle"></i>
            </div>
            <div className="metric-content">
              <div className="metric-number" id="criticalCount" aria-live="polite" aria-atomic="true">
                {displayCount(metrics.criticalCount)}
              </div>
              <div className="metric-label">Critical / High</div>
            </div>
          </div>

          <div className="metric-card active">
            <div className="metric-icon" aria-hidden="true">
              <i className="fas fa-fire"></i>
            </div>
            <div className="metric-content">
              <div className="metric-number" id="activeThreats" aria-live="polite" aria-atomic="true">
                {displayCount(metrics.activeThreats)}
              </div>
              <div className="metric-label">Active Threats</div>
            </div>
          </div>

          <div className="metric-card resolved">
            <div className="metric-icon" aria-hidden="true">
              <i className="fas fa-check-circle"></i>
            </div>
            <div className="metric-content">
              <div className="metric-number" id="resolvedCount" aria-live="polite" aria-atomic="true">
                {displayCount(metrics.resolvedCount)}
              </div>
              <div className="metric-label">Resolved</div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
