import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

export default function Sidebar() {
  const location = useLocation();
  const [theme, setTheme] = useState("dark");

  // Load saved theme or default to dark
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "dark";
    setTheme(savedTheme);
    document.documentElement.setAttribute("data-theme", savedTheme);
  }, []);

  // Toggle theme and store preference
  const toggleTheme = () => {
    const newTheme = theme === "dark" ? "light" : "dark";
    setTheme(newTheme);
    document.documentElement.setAttribute("data-theme", newTheme);
    localStorage.setItem("theme", newTheme);
  };

  return (
    <>
      <style>{`
        :root {
          --bg-color-dark: #282c34;
          --text-color-dark: #cbd3da;
          --accent-color: #61dafb;
          --bg-color-light: #f5f7fa;
          --text-color-light: #333;
          --sidebar-bg: var(--bg-color-dark);
          --sidebar-text: var(--text-color-dark);
          --sidebar-accent: var(--accent-color);
          --sidebar-hover-bg: #3a3f4b;
          --sidebar-active-bg: var(--accent-color);
          --sidebar-active-text: var(--bg-color-dark);
        }
        [data-theme="light"] {
          --sidebar-bg: var(--bg-color-light);
          --sidebar-text: var(--text-color-light);
          --sidebar-accent: var(--accent-color);
          --sidebar-hover-bg: #d1d9e6;
          --sidebar-active-bg: var(--accent-color);
          --sidebar-active-text: var(--bg-color-light);
        }

        .sidebar {
          width: 250px;
          height: 100vh;
          background-color: var(--sidebar-bg);
          color: var(--sidebar-text);
          display: flex;
          flex-direction: column;
          padding: 1.5rem 1rem;
          box-shadow: 2px 0 8px rgba(0,0,0,0.15);
          position: fixed;
          top: 0;
          left: 0;
          overflow-y: auto;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          transition: background-color 0.3s ease, color 0.3s ease;
        }

        .sidebar-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 2rem;
          user-select: none;
        }

        .logo {
          display: flex;
          align-items: center;
          font-size: 1.4rem;
          font-weight: 700;
          color: var(--sidebar-accent);
          gap: 0.5rem;
          transition: color 0.3s ease;
        }

        .logo i {
          font-size: 1.8rem;
        }

        .theme-toggle {
          background: transparent;
          border: none;
          color: var(--sidebar-accent);
          font-size: 1.4rem;
          cursor: pointer;
          transition: color 0.3s ease;
          padding: 0.2rem;
        }

        .theme-toggle:hover {
          color: #21a1f1;
        }

        .nav-menu {
          display: flex;
          flex-direction: column;
          gap: 1rem;
        }

        .nav-item {
          display: flex;
          align-items: center;
          color: var(--sidebar-text);
          text-decoration: none;
          font-size: 1rem;
          padding: 0.75rem 1rem;
          border-radius: 8px;
          transition: background-color 0.3s ease, color 0.3s ease;
          user-select: none;
        }

        .nav-item i {
          margin-right: 1rem;
          font-size: 1.2rem;
          width: 24px;
          text-align: center;
          color: var(--sidebar-accent);
          transition: color 0.3s ease;
        }

        .nav-item:hover {
          background-color: var(--sidebar-hover-bg);
          color: var(--sidebar-active-text);
        }

        .nav-item.active {
          background-color: var(--sidebar-active-bg);
          color: var(--sidebar-active-text);
          font-weight: 700;
        }

        .nav-item.active i {
          color: var(--sidebar-active-text);
        }
      `}</style>

      <nav className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <i className="fas fa-shield-virus"></i>
            <span>VulnScraper</span>
          </div>
          <button
            className="theme-toggle"
            id="themeToggle"
            onClick={toggleTheme}
            aria-label="Toggle Theme"
            title={`Switch to ${theme === "dark" ? "light" : "dark"} theme`}
          >
            {theme === "dark" ? (
              <i className="fas fa-moon"></i>
            ) : (
              <i className="fas fa-sun"></i>
            )}
          </button>
        </div>
        <div className="nav-menu">
          <Link
            to="/dashboard"
            className={`nav-item${location.pathname === "/dashboard" ? " active" : ""}`}
          >
            <i className="fas fa-tachometer-alt"></i>
            <span>Dashboard</span>
          </Link>
          <Link
            to="/dashboard/vulnerabilities"
            className={`nav-item${location.pathname === "/dashboard/vulnerabilities" ? " active" : ""}`}
          >
            <i className="fas fa-bug"></i>
            <span>Vulnerabilities</span>
          </Link>
          <Link
            to="/dashboard/settings"
            className={`nav-item${location.pathname === "/dashboard/settings" ? " active" : ""}`}
          >
            <i className="fas fa-cog"></i>
            <span>Settings</span>
          </Link>
          <Link
            to="/dashboard/reports"
            className={`nav-item${location.pathname === "/dashboard/reports" ? " active" : ""}`}
          >
            <i className="fas fa-chart-bar"></i>
            <span>Reports</span>
          </Link>
        </div>
      </nav>
    </>
  );
}
