import React, { useState } from "react";
import Metrics from "./Metrics";
import Charts from "./Charts";
import RecentVulns from "./RecentVulns";

export default function Dashboard({ onLogout }) {
  const [showAlert, setShowAlert] = useState(true);

  return (
    <>
      <style>{`
        .alerts-section {
          margin: 1.5rem 2rem 2rem;
          max-width: 1200px;
          margin-left: auto;
          margin-right: auto;
        }

        .alert {
          position: relative;
          border-radius: 8px;
          padding: 1rem 1.5rem;
          font-weight: 600;
          display: flex;
          align-items: center;
          gap: 1rem;
          font-size: 1rem;
          box-shadow: 0 3px 8px rgba(0, 0, 0, 0.1);
          color: #fff;
        }

        .alert-critical {
          background-color: #c53030; /* strong red */
          border-left: 6px solid #9b2c2c;
          box-shadow: 0 3px 12px rgba(197, 48, 48, 0.6);
        }

        .alert-critical i {
          font-size: 1.5rem;
          flex-shrink: 0;
        }

        .alert-close {
          position: absolute;
          top: 50%;
          right: 0.75rem;
          transform: translateY(-50%);
          background: transparent;
          border: none;
          font-size: 1.2rem;
          color: #fde2e2;
          cursor: pointer;
          padding: 0;
          line-height: 1;
          transition: color 0.3s ease;
          font-weight: bold;
          user-select: none;
        }

        .alert-close:hover {
          color: #fff;
        }

        .dashboard-view {
          max-width: 1200px;
          margin-left: auto;
          margin-right: auto;
          padding: 0 2rem 2rem;
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
          gap: 2rem;
        }

        @media (max-width: 640px) {
          .dashboard-view {
            grid-template-columns: 1fr;
            padding: 0 1rem 1.5rem;
          }
        }
      `}</style>

      <div className="alerts-section" id="alertsSection">
        {showAlert && (
          <div className="alert alert-critical" role="alert">
            <i className="fas fa-exclamation-triangle" aria-hidden="true"></i>
            <span>New Critical vulnerability detected: Cisco IOS XE Web UI Privilege Escalation</span>
            <button
              className="alert-close"
              aria-label="Close alert"
              onClick={() => setShowAlert(false)}
            >
              &times;
            </button>
          </div>
        )}
      </div>

      <div className="dashboard-view" id="dashboardView">
        <Metrics />
        <Charts />
        <RecentVulns />
      </div>
    </>
  );
}
