import React from "react";
import { Link, useLocation } from "react-router-dom";

export default function Sidebar() {
  const location = useLocation();

  return (
    <>
      <style>{`
        .sidebar {
          width: 250px;
          height: 100vh;
          background-color: #282c34;
          color: #cbd3da;
          display: flex;
          flex-direction: column;
          padding: 1.5rem 1rem;
          box-shadow: 2px 0 8px rgba(0,0,0,0.15);
          position: fixed;
          top: 0;
          left: 0;
          overflow-y: auto;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        .sidebar-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 2rem;
          user-select: none;
        }

        .logo {
          display: flex;
          align-items: center;
          font-size: 1.4rem;
          font-weight: 700;
          color: #61dafb;
          gap: 0.5rem;
        }

        .logo i {
          font-size: 1.8rem;
        }

        .theme-toggle {
          background: transparent;
          border: none;
          color: #61dafb;
          font-size: 1.4rem;
          cursor: pointer;
          transition: color 0.3s ease;
          padding: 0.2rem;
        }

        .theme-toggle:hover {
          color: #21a1f1;
        }

        .nav-menu {
          display: flex;
          flex-direction: column;
          gap: 1rem;
        }

        .nav-item {
          display: flex;
          align-items: center;
          color: #cbd3da;
          text-decoration: none;
          font-size: 1rem;
          padding: 0.75rem 1rem;
          border-radius: 8px;
          transition: background-color 0.3s ease, color 0.3s ease;
          user-select: none;
        }

        .nav-item i {
          margin-right: 1rem;
          font-size: 1.2rem;
          width: 24px;
          text-align: center;
          color: #61dafb;
        }

        .nav-item:hover {
          background-color: #3a3f4b;
          color: #ffffff;
        }

        .nav-item.active {
          background-color: #61dafb;
          color: #282c34;
          font-weight: 700;
        }

        .nav-item.active i {
          color: #282c34;
        }
      `}</style>

      <nav className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <i className="fas fa-shield-virus"></i>
            <span>VulnScraper</span>
          </div>
          <button className="theme-toggle" id="themeToggle" title="Toggle Theme">
            <i className="fas fa-moon"></i>
          </button>
        </div>
        <div className="nav-menu">
          <Link
            to="/dashboard"
            className={`nav-item${location.pathname === "/dashboard" ? " active" : ""}`}
          >
            <i className="fas fa-tachometer-alt"></i>
            <span>Dashboard</span>
          </Link>
          <Link
            to="/dashboard/vulnerabilities"
            className={`nav-item${location.pathname === "/dashboard/vulnerabilities" ? " active" : ""}`}
          >
            <i className="fas fa-bug"></i>
            <span>Vulnerabilities</span>
          </Link>
          <Link
            to="/dashboard/settings"
            className={`nav-item${location.pathname === "/dashboard/settings" ? " active" : ""}`}
          >
            <i className="fas fa-cog"></i>
            <span>Settings</span>
          </Link>
          <Link
            to="/dashboard/reports"
            className={`nav-item${location.pathname === "/dashboard/reports" ? " active" : ""}`}
          >
            <i className="fas fa-chart-bar"></i>
            <span>Reports</span>
          </Link>
        </div>
      </nav>
    </>
  );
}
