import React, { useState, useEffect } from "react";
import { Doughnut, Line } from "react-chartjs-2";
import {
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";
import axios from "axios";

import Chart from "chart.js/auto";
Chart.register(
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

export default function Charts() {
  const [severityData, setSeverityData] = useState({
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: ["#ff3333", "#ffb347", "#ffe066", "#6fd3c3"],
      },
    ],
  });

  const [trendsData, setTrendsData] = useState({
    labels: [],
    datasets: [
      {
        label: "New Vulnerabilities",
        data: [],
        backgroundColor: "rgba(97, 218, 251, 0.15)",
        borderColor: "#61dafb",
        fill: true,
        tension: 0.3,
      },
    ],
  });

  const [loading, setLoading] = useState(true);
  const [isDarkTheme, setIsDarkTheme] = useState(false);

  useEffect(() => {
    // Detect theme by checking <body> class
    setIsDarkTheme(document.body.classList.contains("light-theme") === false);

    const fetchChartData = async () => {
      try {
        const [severityResponse, trendsResponse] = await Promise.all([
          axios.get("/api/vulnerabilities/charts/severity"),
          axios.get("/api/vulnerabilities/charts/trends"),
        ]);

        setSeverityData({
          labels: severityResponse.data.labels,
          datasets: [
            {
              data: severityResponse.data.data,
              backgroundColor: ["#ff3333", "#ffb347", "#ffe066", "#6fd3c3"],
            },
          ],
        });

        setTrendsData({
          labels: trendsResponse.data.labels,
          datasets: [
            {
              label: "New Vulnerabilities",
              data: trendsResponse.data.data,
              backgroundColor: "rgba(97, 218, 251, 0.15)",
              borderColor: "#61dafb",
              fill: true,
              tension: 0.3,
            },
          ],
        });
      } catch (error) {
        console.error("Error fetching chart data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchChartData();
  }, []);

  // Dynamic Chart.js options for dark/light
  const getCommonOptions = () => ({
    responsive: true,
    plugins: {
      legend: {
        labels: {
          color: isDarkTheme ? "#cbd3da" : "#282c34",
        },
      },
      tooltip: {
        backgroundColor: isDarkTheme ? "#282c34" : "#fff",
        titleColor: isDarkTheme ? "#61dafb" : "#000",
        bodyColor: isDarkTheme ? "#cbd3da" : "#000",
      },
    },
    scales: {
      x: {
        ticks: {
          color: isDarkTheme ? "#cbd3da" : "#282c34",
        },
        grid: {
          color: isDarkTheme ? "#444" : "#eee",
        },
      },
      y: {
        ticks: {
          color: isDarkTheme ? "#cbd3da" : "#282c34",
          beginAtZero: true,
          stepSize: 1,
        },
        grid: {
          color: isDarkTheme ? "#444" : "#eee",
        },
      },
    },
  });

  return (
    <>
      <style>{`
        .charts-section {
          max-width: 1200px;
          margin: 0 auto 2rem;
          display: flex;
          gap: 2rem;
          flex-wrap: wrap;
          padding: 0 1rem;
          background: none;
        }
        .chart-container {
          flex: 1 1 480px;
          background-color: var(--card-bg, #fff);
          border-radius: 14px;
          box-shadow: var(--shadow-md, 0 6px 18px #61dafb22);
          padding: 1.5rem 1.8rem;
          transition: background-color 0.4s ease;
        }
        .card__header {
          border-bottom: 2px solid #61dafb;
          padding-bottom: 0.5rem;
          margin-bottom: 1rem;
          font-weight: 700;
          font-size: 1.3rem;
          color: var(--text-primary, #282c34);
          transition: color 0.4s ease;
        }
        body:not(.light-theme) .card__header {
          color: #cbd3da;
          border-color: #1fb8cdbb;
        }
        .card__body {
          height: 300px;
        }
        @media (max-width: 600px) {
          .charts-section {
            flex-direction: column;
            padding: 0 0.5rem;
          }
          .chart-container {
            flex: 1 1 100%;
          }
          .card__body {
            height: 270px;
          }
        }
      `}</style>
      {loading ? (
        <div className="charts-section">Loading charts...</div>
      ) : (
        <section className="charts-section" aria-label="Vulnerability charts">
          <div className="chart-container">
            <div className="card__header">Severity Distribution</div>
            <div className="card__body">
              <Doughnut data={severityData} options={getCommonOptions()} role="img" aria-label="Severity distribution doughnut chart"/>
            </div>
          </div>
          <div className="chart-container">
            <div className="card__header">Vulnerability Trends</div>
            <div className="card__body">
              <Line data={trendsData} options={getCommonOptions()} role="img" aria-label="Vulnerability trends line chart"/>
            </div>
          </div>
        </section>
      )}
    </>
  );
}
