  }
  let paginationHTML = '';

  // Previous button
  if (appState.currentPage > 1) {
    paginationHTML += `<button onclick="changePage(${appState.currentPage - 1})">Previous</button>`;
  }

  // Page numbers
  const startPage = Math.max(1, appState.currentPage - 2);
  const endPage = Math.min(totalPages, appState.currentPage + 2);
  for (let i = startPage; i <= endPage; i++) {
    paginationHTML += `
      <button class="${i === appState.currentPage ? 'active' : ''}" 
              onclick="changePage(${i})">${i}</button>
    `;
  }

  // Next button
  if (appState.currentPage < totalPages) {
