  const includeLow = document.getElementById('includeLow').checked;

  // Filter vulnerabilities based on criteria
  const filteredData = filterVulnerabilitiesForReport({
    startDate,
    endDate,
    includeCritical,
    includeHigh,
    includeMedium,
    includeLow
  });
  const csvContent = generateCSVContent(filteredData);
  downloadCSV(csvContent, `vulnerability_data_${new Date().toISOString().split('T')[0]}.csv`);
  showNotification('CSV report exported successfully!', 'success');

  // Add to report history
  addToReportHistory('csv_export', 'CSV');
}
function generateQuickReport(type) {
  let filteredData = [];
  let reportName = '';
  switch (type) {
    case 'active':
      filteredData = vulnerabilityData.vulnerabilities.filter(v => v.status === 'Active');
      reportName = 'Active Threats Report';
      break;
    case 'critical':
      filteredData = vulnerabilityData.vulnerabilities.filter(v => v.severity === 'Critical' || v.severity === 'High');
      reportName = 'Critical Issues Report';
      break;
    case 'vendor':
      filteredData = vulnerabilityData.vulnerabilities;
      reportName = 'Vendor Summary Report';
      break;
    case 'monthly':
      const oneMonthAgo = new Date();
      oneMonthAgo.setMonth(oneMonthAgo.getMonth() - 1);
      filteredData = vulnerabilityData.vulnerabilities.filter(v => new Date(v.published) >= oneMonthAgo);
      reportName = 'Monthly Report';
      break;
  }
  showNotification(`Generating ${reportName}...`, 'info');
  setTimeout(() => {
    const csvContent = generateCSVContent(filteredData);
    downloadCSV(csvContent, `${type}_report_${new Date().toISOString().split('T')[0]}.csv`);
    showNotification(`${reportName} generated successfully!`, 'success');

    // Add to report history
    addToReportHistory(type, 'CSV');
  }, 1000);
}
function filterVulnerabilitiesForReport(criteria) {
  return vulnerabilityData.vulnerabilities.filter(vuln => {
    const vulnDate = new Date(vuln.published);
    const startDate = new Date(criteria.startDate);
    const endDate = new Date(criteria.endDate);

    // Check date range
    const inDateRange = vulnDate >= startDate && vulnDate <= endDate;

    // Check severity
    let severityMatch = false;
    if (criteria.includeCritical && vuln.severity === 'Critical') severityMatch = true;
    if (criteria.includeHigh && vuln.severity === 'High') severityMatch = true;
    if (criteria.includeMedium && vuln.severity === 'Medium') severityMatch = true;
    if (criteria.includeLow && vuln.severity === 'Low') severityMatch = true;
    return inDateRange && severityMatch;
  });
}
function getReportTypeName(type) {
  const names = {
    'summary': 'Executive Summary',
    'detailed': 'Detailed Vulnerability Report',
    'vendor': 'Vendor Analysis Report',
    'trends': 'Trend Analysis Report'
  };
  return names[type] || 'Report';
}
function generateReportContent(type, data) {
  // This would generate actual PDF content in a real application
  return `Report Type: ${getReportTypeName(type)}\nVulnerabilities: ${data.length}\nGenerated: ${new Date().toLocaleString()}`;
}
function downloadPDF(content, filename) {
  // Mock PDF download - in a real application, this would generate an actual PDF
  const blob = new Blob([content], {
    type: 'application/pdf'
  });
  const link = document.createElement('a');
  if (link.download !== undefined) {
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', filename);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }
}
function addToReportHistory(type, format) {
  // This would add to actual report history in a real application
  // For demo purposes, we just show the notification
}

// Charts
function setupCharts() {
  setupSeverityChart();
  setupTrendsChart();
}
function setupSeverityChart() {
  const ctx = document.getElementById('severityChart');
  if (!ctx) return;
  const stats = vulnerabilityData.statistics;
  new Chart(ctx, {
    type: 'doughnut',
    data: {
      labels: ['Critical', 'High', 'Medium', 'Low'],
      datasets: [{
        data: [stats.critical, stats.high, stats.medium, stats.low],
        backgroundColor: ['#1FB8CD', '#FFC185', '#B4413C', '#ECEBD5'],
        borderWidth: 2,
        borderColor: getComputedStyle(document.documentElement).getPropertyValue('--color-surface')
      }]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          position: 'bottom',
          labels: {
            usePointStyle: true,
            color: getComputedStyle(document.documentElement).getPropertyValue('--color-text')
          }
        }
      }
    }
  });
}
function setupTrendsChart() {
  const ctx = document.getElementById('trendsChart');
  if (!ctx) return;
  const trends = vulnerabilityData.trends;
  new Chart(ctx, {
    type: 'line',
    data: {
      labels: trends.map(t => new Date(t.date).toLocaleDateString()),
      datasets: [{
        label: 'New Vulnerabilities',
        data: trends.map(t => t.count),
        borderColor: '#1FB8CD',
        backgroundColor: 'rgba(31, 184, 205, 0.1)',
        fill: true,
        tension: 0.4
      }]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          labels: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--color-text')
          }
        }
      },
      scales: {
        x: {
          ticks: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--color-text-secondary')
          },
          grid: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--color-border')
          }
        },
        y: {
          beginAtZero: true,
          ticks: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--color-text-secondary'),
            stepSize: 1
          },
          grid: {
            color: getComputedStyle(document.documentElement).getPropertyValue('--color-border')
          }
        }
      }
    }
  });
}

// Utility Functions
function getStatusClass(status) {
  switch (status) {
    case 'Active':
      return 'error';
    case 'Resolved':
      return 'success';
    case 'Under Investigation':
      return 'warning';
    default:
      return 'info';
  }
}
function formatDate(dateString) {
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
}
function showNotification(message, type = 'info') {
  // Create notification element
  const notification = document.createElement('div');
  notification.className = `notification notification--${type}`;
  notification.innerHTML = `
    <span>${message}</span>
    <button class="notification-close">&times;</button>
  `;

  // Add to DOM
  document.body.appendChild(notification);

  // Auto-remove after 5 seconds
  setTimeout(() => {
    if (notification.parentNode) {
      notification.parentNode.removeChild(notification);
    }
  }, 5000);

  // Add close event listener
  notification.querySelector('.notification-close').addEventListener('click', () => {
    if (notification.parentNode) {
      notification.parentNode.removeChild(notification);
    }
  });
}
function closeAlert(e) {
  const alert = e.target.closest('.alert');
  if (alert) {
    alert.style.display = 'none';
  }
}

// Real-time Updates Simulation
function simulateRealTimeUpdates() {
  // Simulate new vulnerability every 30 seconds
  setInterval(() => {
    if (Math.random() < 0.3) {
      // 30% chance
      simulateNewVulnerability();
    }
  }, 30000);

  // Simulate metrics updates
  setInterval(() => {
    updateMetricsWithAnimation();
  }, 15000);
}
function simulateNewVulnerability() {
  const newVuln = {
    id: Date.now().toString(),
    cve: `CVE-2024-${Math.floor(Math.random() * 9000) + 1000}`,
    title: `New ${getRandomVendor()} Security Vulnerability Detected`,
    description: "A new security vulnerability has been detected by our automated scanning system.",
    severity: getRandomSeverity(),
    cvss: (Math.random() * 4 + 6).toFixed(1),
    // 6.0 - 10.0
    vendor: getRandomVendor(),
    product: "Product Suite",
    version: "Latest",
    status: "Active",
    published: new Date().toISOString(),
    references: [`https://security.${getRandomVendor().toLowerCase()}.com/advisory`]
  };
  vulnerabilityData.vulnerabilities.unshift(newVuln);
  vulnerabilityData.statistics.total++;
  vulnerabilityData.statistics.active++;
  if (newVuln.severity === 'Critical') {
    vulnerabilityData.statistics.critical++;
  } else if (newVuln.severity === 'High') {
    vulnerabilityData.statistics.high++;
  } else if (newVuln.severity === 'Medium') {
    vulnerabilityData.statistics.medium++;
  }

  // Show alert for critical vulnerabilities
  if (newVuln.severity === 'Critical') {
    showCriticalAlert(newVuln);
  }

  // Update displays
  applyFilters();
  renderDashboard();
  showNotification(`New ${newVuln.severity} vulnerability detected: ${newVuln.cve}`, 'warning');
}
function showCriticalAlert(vulnerability) {
  const alertsSection = document.getElementById('alertsSection');
  const newAlert = document.createElement('div');
  newAlert.className = 'alert alert-critical fade-in';
  newAlert.innerHTML = `
    <i class="fas fa-exclamation-triangle"></i>
    <span>New Critical vulnerability detected: ${vulnerability.title}</span>
    <button class="alert-close">&times;</button>
  `;
  alertsSection.appendChild(newAlert);

  // Add close event listener
  newAlert.querySelector('.alert-close').addEventListener('click', closeAlert);

  // Auto-remove after 10 seconds
  setTimeout(() => {
    if (newAlert.parentNode) {
      newAlert.parentNode.removeChild(newAlert);
    }
  }, 10000);
}
function updateMetricsWithAnimation() {
  const metrics = document.querySelectorAll('.metric-number');
  metrics.forEach(metric => {
    metric.classList.add('fade-in');
    setTimeout(() => {
      metric.classList.remove('fade-in');
    }, 500);
  });
}
function getRandomVendor() {
  const vendors = ['Cisco', 'Microsoft', 'VMware', 'Oracle', 'Adobe', 'IBM', 'SAP'];
  return vendors[Math.floor(Math.random() * vendors.length)];
}
function getRandomSeverity() {
  const severities = ['Critical', 'High', 'Medium', 'Low'];
  const weights = [0.1, 0.3, 0.4, 0.2]; // More medium/high, fewer critical
  const random = Math.random();
  let sum = 0;
  for (let i = 0; i < weights.length; i++) {
    sum += weights[i];
    if (random < sum) {
      return severities[i];
    }
  }
  return 'Medium';
}

sourcesContent:
// Application Data
import { Line, Bar, Doughnut } from 'react-chartjs-2';

const vulnerabilityData = {
  vulnerabilities: [
    {
      id: "1",
      cve: "CVE-2024-1234",
      title: "Cisco IOS XE Web UI Privilege Escalation Vulnerability",
      description: "A vulnerability in the web-based management interface of Cisco IOS XE software could allow an authenticated, remote attacker to perform a privilege escalation attack.",
      severity: "Critical",
      cvss: 9.8,
      vendor: "Cisco",
      product: "IOS XE",
      version: "17.6.x",
      status: "Active",
      published: "2024-10-05T08:00:00Z",
      references: ["https://sec.cisco.com/advisory/1234"],
      impact: {
        confidentiality: "Complete",
        integrity: "Complete", 
        availability: "Complete"
      }
    },
    {
      id: "2",
      cve: "CVE-2024-5678",
      title: "Juniper Networks Junos OS Command Injection",
      description: "A command injection vulnerability in Juniper Networks Junos OS allows remote code execution.",
      severity: "High",
      cvss: 8.1,
      vendor: "Juniper",
      product: "Junos OS",
      version: "22.4R1",
      status: "Under Investigation",
      published: "2024-10-03T14:30:00Z",
      references: ["https://kb.juniper.net/advisory/5678"]
    },
    {
      id: "3",
      cve: "CVE-2024-9012",
      title: "Honeywell Experion PKS Buffer Overflow",
      description: "A buffer overflow vulnerability in Honeywell Experion PKS could lead to denial of service.",
      severity: "Medium",
      cvss: 5.3,
      vendor: "Honeywell",
      product: "Experion PKS",
      version: "C200.2",
      status: "Resolved",
      published: "2024-09-28T10:15:00Z",
      references: ["https://process.honeywell.com/security/9012"]
    },
    {
      id: "4",
      cve: "CVE-2024-3456",
      title: "Microsoft Windows Kernel Information Disclosure",
      description: "An information disclosure vulnerability exists in the Windows kernel that could allow elevation of privilege.",
      severity: "High",
      cvss: 7.8,
      vendor: "Microsoft",
      product: "Windows",
      version: "11 22H2",
      status: "Active",
      published: "2024-10-01T16:45:00Z",
      references: ["https://msrc.microsoft.com/update-guide/3456"]
    },
    {
      id: "5",
      cve: "CVE-2024-7890",
      title: "VMware vCenter Server Authentication Bypass",
      description: "An authentication bypass vulnerability in VMware vCenter Server could allow remote attackers to gain administrative access.",
      severity: "Critical",
      cvss: 9.1,
      vendor: "VMware",
      product: "vCenter Server",
      version: "7.0 U3",
      status: "Active", 
      published: "2024-09-25T12:20:00Z",
      references: ["https://www.vmware.com/security/advisories/7890"]
    }
  ],
  statistics: {
    total: 5,
    critical: 2,
    high: 2,
    medium: 1,
    low: 0,
    active: 3,
    resolved: 1,
    investigating: 1
  },
  trends: [
    {date: "2024-09-25", count: 1},
    {date: "2024-09-28", count: 1}, 
    {date: "2024-10-01", count: 1},
    {date: "2024-10-03", count: 1},
    {date: "2024-10-05", count: 1}
  ],
  alerts: [
    {
      id: "alert1",
      message: "New Critical vulnerability detected: Cisco IOS XE Web UI Privilege Escalation",
      severity: "critical",
      timestamp: "2024-10-05T08:00:00Z"
    }
  ]
};

// Application State
let appState = {
  currentView: 'dashboard',
  filteredVulnerabilities: [...vulnerabilityData.vulnerabilities],
  sortField: null,
  sortDirection: 'asc',
  currentPage: 1,
  itemsPerPage: 10,
  filters: {
    search: '',
    severity: '',
    vendor: '',
    status: ''
  },
  selectedVulnerabilities: []
};

// Initialize Application
document.addEventListener('DOMContentLoaded', function() {
  initializeApp();
});

function initializeApp() {
  setupEventListeners();
  updateCurrentTime();
  setInterval(updateCurrentTime, 1000);
  renderDashboard();
  renderVulnerabilitiesTable();
  setupCharts();
  simulateRealTimeUpdates();
}

// Event Listeners
function setupEventListeners() {
  // Navigation
  document.querySelectorAll('.nav-item').forEach(item => {
    item.addEventListener('click', handleNavigation);
  });

  // Theme toggle
  document.getElementById('themeToggle').addEventListener('click', toggleTheme);

  // Alert close
  document.querySelectorAll('.alert-close').forEach(btn => {
    btn.addEventListener('click', closeAlert);
  });

  // Search and filters
  document.getElementById('searchInput')?.addEventListener('input', handleSearch);
  document.getElementById('severityFilter')?.addEventListener('change', handleFilter);
  document.getElementById('vendorFilter')?.addEventListener('change', handleFilter);
  document.getElementById('statusFilter')?.addEventListener('change', handleFilter);

  // Table sorting
  document.querySelectorAll('.sortable').forEach(header => {
    header.addEventListener('click', handleSort);
  });

  // Select all checkbox
  document.getElementById('selectAll')?.addEventListener('change', handleSelectAll);

  // Export button
  document.getElementById('exportBtn')?.addEventListener('click', exportToCSV);

  // Modal events
  document.querySelectorAll('.modal-close').forEach(btn => {
    btn.addEventListener('click', closeModal);
  });

  document.querySelector('.modal-backdrop')?.addEventListener('click', closeModal);

  document.getElementById('modalResolve')?.addEventListener('click', resolveVulnerability);
  document.getElementById('modalExport')?.addEventListener('click', exportSingleVulnerability);

  // Reports events
  document.getElementById('generateReportBtn')?.addEventListener('click', generateReport);
  document.getElementById('generateCSVBtn')?.addEventListener('click', generateCSVReport);
}

// Navigation
function handleNavigation(e) {
  e.preventDefault();
  const view = e.currentTarget.dataset.view;

  // Update active nav item
  document.querySelectorAll('.nav-item').forEach(item => item.classList.remove('active'));
  e.currentTarget.classList.add('active');

  // Update page title
  const pageTitle = document.querySelector('.page-title');
  pageTitle.textContent = view.charAt(0).toUpperCase() + view.slice(1);

  // Show appropriate view
  document.querySelectorAll('.view').forEach(v => v.classList.remove('active'));
  document.getElementById(view + 'View').classList.add('active');

  appState.currentView = view;

  // Load view-specific data
  if (view === 'vulnerabilities') {
    renderVulnerabilitiesTable();
  } else if (view === 'reports') {
    setupReportsPage();
  }
}

// Theme Toggle
function toggleTheme() {
  const currentTheme = document.documentElement.getAttribute('data-color-scheme');
  const newTheme = currentTheme === 'dark' ? 'light' : 'dark';

  document.documentElement.setAttribute('data-color-scheme', newTheme);

  const themeIcon = document.querySelector('#themeToggle i');
  themeIcon.className = newTheme === 'dark' ? 'fas fa-sun' : 'fas fa-moon';
}

// Time Update
function updateCurrentTime() {
  const now = new Date();
  const options = {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  };

  document.getElementById('currentTime').textContent = now.toLocaleDateString('en-US', options);
}

// Dashboard Rendering
function renderDashboard() {
  const stats = vulnerabilityData.statistics;

  document.getElementById('totalVulns').textContent = stats.total;
  document.getElementById('criticalCount').textContent = stats.critical + stats.high;
  document.getElementById('activeThreats').textContent = stats.active;
  document.getElementById('resolvedCount').textContent = stats.resolved;

  renderRecentVulnerabilitiesTable();
}

function renderRecentVulnerabilitiesTable() {
  const tbody = document.getElementById('recentVulnsTable');
  const recentVulns = vulnerabilityData.vulnerabilities.slice(0, 3);

  tbody.innerHTML = recentVulns.map(vuln => `
    <tr>
      <td><code>${vuln.cve}</code></td>
      <td class="title-cell" title="${vuln.title}">
        ${vuln.title.length > 50 ? vuln.title.substring(0, 50) + '...' : vuln.title}
      </td>
      <td>${vuln.vendor}</td>
      <td>
        <span class="severity-badge severity-${vuln.severity.toLowerCase()}">
          ${vuln.severity}
        </span>
      </td>
      <td><strong>${vuln.cvss}</strong></td>
      <td>
        <span class="status status--${getStatusClass(vuln.status)}">
          ${vuln.status}
        </span>
      </td>
      <td>${formatDate(vuln.published)}</td>
      <td>
        <div class="action-buttons">
          <button class="action-btn action-btn--view" onclick="viewVulnerability('${vuln.id}')">
            <i class="fas fa-eye"></i>
          </button>
        </div>
      </td>
    </tr>
  `).join('');
}

// Vulnerabilities Table
function renderVulnerabilitiesTable() {
  const tbody = document.getElementById('vulnerabilitiesTable');
  if (!tbody) return;

  const startIndex = (appState.currentPage - 1) * appState.itemsPerPage;
  const endIndex = startIndex + appState.itemsPerPage;
  const paginatedVulns = appState.filteredVulnerabilities.slice(startIndex, endIndex);

  tbody.innerHTML = paginatedVulns.map(vuln => `
    <tr>
      <td>
        <input type="checkbox" class="vuln-checkbox" value="${vuln.id}" 
               ${appState.selectedVulnerabilities.includes(vuln.id) ? 'checked' : ''}>
      </td>
      <td><code>${vuln.cve}</code></td>
      <td class="title-cell" title="${vuln.title}">
        ${vuln.title.length > 60 ? vuln.title.substring(0, 60) + '...' : vuln.title}
      </td>
      <td>${vuln.vendor}</td>
      <td>
        <span class="severity-badge severity-${vuln.severity.toLowerCase()}">
          ${vuln.severity}
        </span>
      </td>
      <td><strong>${vuln.cvss}</strong></td>
      <td>
        <span class="status status--${getStatusClass(vuln.status)}">
          ${vuln.status}
        </span>
      </td>
      <td>${formatDate(vuln.published)}</td>
      <td>
        <div class="action-buttons">
          <button class="action-btn action-btn--view" onclick="viewVulnerability('${vuln.id}')">
            <i class="fas fa-eye"></i>
          </button>
          ${vuln.status !== 'Resolved' ? `
            <button class="action-btn action-btn--resolve" onclick="markAsResolved('${vuln.id}')">
              <i class="fas fa-check"></i>
            </button>
          ` : ''}
        </div>
      </td>
    </tr>
  `).join('');

  updateTableInfo();
  updatePagination();

  // Add event listeners for checkboxes
  document.querySelectorAll('.vuln-checkbox').forEach(checkbox => {
    checkbox.addEventListener('change', handleVulnerabilitySelection);
  });
}

// Search and Filter
function handleSearch(e) {
  appState.filters.search = e.target.value.toLowerCase();
  applyFilters();
}

function handleFilter(e) {
  const filterId = e.target.id;
  const filterValue = e.target.value;

  switch(filterId) {
    case 'severityFilter':
      appState.filters.severity = filterValue;
      break;
    case 'vendorFilter':
      appState.filters.vendor = filterValue;
      break;
    case 'statusFilter':
      appState.filters.status = filterValue;
      break;
  }

  applyFilters();
}

function applyFilters() {
  appState.filteredVulnerabilities = vulnerabilityData.vulnerabilities.filter(vuln => {
    const matchesSearch = !appState.filters.search || 
      vuln.cve.toLowerCase().includes(appState.filters.search) ||
      vuln.title.toLowerCase().includes(appState.filters.search) ||
      vuln.description.toLowerCase().includes(appState.filters.search);

    const matchesSeverity = !appState.filters.severity || vuln.severity === appState.filters.severity;
    const matchesVendor = !appState.filters.vendor || vuln.vendor === appState.filters.vendor;
    const matchesStatus = !appState.filters.status || vuln.status === appState.filters.status;

    return matchesSearch && matchesSeverity && matchesVendor && matchesStatus;
  });

  appState.currentPage = 1;
  renderVulnerabilitiesTable();
}

// Sorting
function handleSort(e) {
  const field = e.target.dataset.sort;

  if (appState.sortField === field) {
    appState.sortDirection = appState.sortDirection === 'asc' ? 'desc' : 'asc';
  } else {
    appState.sortField = field;
    appState.sortDirection = 'asc';
  }

  appState.filteredVulnerabilities.sort((a, b) => {
    let aValue = a[field];
    let bValue = b[field];

    // Handle special cases
    if (field === 'published') {
      aValue = new Date(aValue);
      bValue = new Date(bValue);
    } else if (field === 'cvss') {
      aValue = parseFloat(aValue);
      bValue = parseFloat(bValue);
    } else {
      aValue = aValue?.toString().toLowerCase() || '';
      bValue = bValue?.toString().toLowerCase() || '';
    }

    if (aValue < bValue) return appState.sortDirection === 'asc' ? -1 : 1;
    if (aValue > bValue) return appState.sortDirection === 'asc' ? 1 : -1;
    return 0;
  });

  renderVulnerabilitiesTable();

  // Update sort indicators
  document.querySelectorAll('.sortable').forEach(header => {
    header.classList.remove('sort-asc', 'sort-desc');
  });

  if (appState.sortField) {
    e.target.classList.add(`sort-${appState.sortDirection}`);
  }
}

// Selection
function handleSelectAll(e) {
  const isChecked = e.target.checked;
  appState.selectedVulnerabilities = isChecked 
    ? appState.filteredVulnerabilities.map(v => v.id)
    : [];

  document.querySelectorAll('.vuln-checkbox').forEach(checkbox => {
    checkbox.checked = isChecked;
  });
}

function handleVulnerabilitySelection(e) {
  const vulnId = e.target.value;
  const isChecked = e.target.checked;

  if (isChecked) {
    appState.selectedVulnerabilities.push(vulnId);
  } else {
    appState.selectedVulnerabilities = appState.selectedVulnerabilities.filter(id => id !== vulnId);
  }

  // Update select all checkbox
  const selectAllCheckbox = document.getElementById('selectAll');
  const totalVisible = appState.filteredVulnerabilities.length;
  const selectedVisible = appState.selectedVulnerabilities.length;

  selectAllCheckbox.checked = selectedVisible === totalVisible && totalVisible > 0;
  selectAllCheckbox.indeterminate = selectedVisible > 0 && selectedVisible < totalVisible;
}

// Pagination
function updateTableInfo() {
  const startIndex = (appState.currentPage - 1) * appState.itemsPerPage + 1;
  const endIndex = Math.min(startIndex + appState.itemsPerPage - 1, appState.filteredVulnerabilities.length);
  const total = appState.filteredVulnerabilities.length;

  document.getElementById('tableInfo').textContent = `${startIndex}-${endIndex} of ${total}`;
}

function updatePagination() {
  const totalPages = Math.ceil(appState.filteredVulnerabilities.length / appState.itemsPerPage);
  const pagination = document.getElementById('pagination');

  if (!pagination || totalPages <= 1) {
    if (pagination) pagination.innerHTML = '';
    return;
  }

  let paginationHTML = '';

  // Previous button
  if (appState.currentPage > 1) {
    paginationHTML += `<button onclick="changePage(${appState.currentPage - 1})">Previous</button>`;
  }

  // Page numbers
  const startPage = Math.max(1, appState.currentPage - 2);
  const endPage = Math.min(totalPages, appState.currentPage + 2);

  for (let i = startPage; i <= endPage; i++) {
    paginationHTML += `
      <button class="${i === appState.currentPage ? 'active' : ''}" 
              onclick="changePage(${i})">${i}</button>
    `;
  }

  // Next button
  if (appState.currentPage < totalPages) {
    paginationHTML += `<button onclick="changePage(${appState.currentPage + 1})">Next</button>`;
  }

  pagination.innerHTML = paginationHTML;
}

function changePage(page) {
  appState.currentPage = page;
  renderVulnerabilitiesTable();
}

