function resolveVulnerability() {
  const modal = document.getElementById('vulnerabilityModal');
  const vulnId = modal.dataset.vulnerabilityId;
  markAsResolved(vulnId);
  closeModal();
}

function exportSingleVulnerability() {
  const modal = document.getElementById('vulnerabilityModal');
  const vulnId = modal.dataset.vulnerabilityId;
  const vulnerability = vulnerabilityData.vulnerabilities.find(v => v.id === vulnId);

  if (vulnerability) {
    const csvContent = generateCSVContent([vulnerability]);
    downloadCSV(csvContent, `vulnerability_${vulnerability.cve}.csv`);
  }
}

// Actions
function markAsResolved(id) {
  const vulnerability = vulnerabilityData.vulnerabilities.find(v => v.id === id);
  if (vulnerability) {
    vulnerability.status = 'Resolved';

    // Update statistics
    vulnerabilityData.statistics.active--;
    vulnerabilityData.statistics.resolved++;
    vulnerabilityData.statistics.investigating = Math.max(0, vulnerabilityData.statistics.investigating - 1);

    // Re-render relevant sections
    renderDashboard();
    renderVulnerabilitiesTable();

    // Show success message
    showNotification('Vulnerability marked as resolved successfully!', 'success');
  }
}

// Export Functions
function exportToCSV() {
  const dataToExport = appState.selectedVulnerabilities.length > 0
    ? vulnerabilityData.vulnerabilities.filter(v => appState.selectedVulnerabilities.includes(v.id))
    : appState.filteredVulnerabilities;

  const csvContent = generateCSVContent(dataToExport);
  downloadCSV(csvContent, 'vulnerabilities_export.csv');
}

function generateCSVContent(vulnerabilities) {
  const headers = ['CVE', 'Title', 'Description', 'Severity', 'CVSS', 'Vendor', 'Product', 'Version', 'Status', 'Published'];
  const rows = vulnerabilities.map(vuln => [
    vuln.cve,
    `"${vuln.title}"`,
    `"${vuln.description}"`,
    vuln.severity,
    vuln.cvss,
    vuln.vendor,
    vuln.product || '',
    vuln.version || '',
    vuln.status,
    vuln.published
  ]);

  return [headers, ...rows].map(row => row.join(',')).join('\n');
}

function downloadCSV(content, filename) {
  const blob = new Blob([content], { type: 'text/csv;charset=utf-8;' });
  const link = document.createElement('a');

  if (link.download !== undefined) {
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', filename);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }
}

