function generateQuickReport(type) {
  let filteredData = [];
  let reportName = '';

  switch(type) {
    case 'active':
      filteredData = vulnerabilityData.vulnerabilities.filter(v => v.status === 'Active');
      reportName = 'Active Threats Report';
      break;
    case 'critical':
      filteredData = vulnerabilityData.vulnerabilities.filter(v => v.severity === 'Critical' || v.severity === 'High');
      reportName = 'Critical Issues Report';
      break;
    case 'vendor':
      filteredData = vulnerabilityData.vulnerabilities;
      reportName = 'Vendor Summary Report';
      break;
    case 'monthly':
      const oneMonthAgo = new Date();
      oneMonthAgo.setMonth(oneMonthAgo.getMonth() - 1);
      filteredData = vulnerabilityData.vulnerabilities.filter(v => 
        new Date(v.published) >= oneMonthAgo
      );
      reportName = 'Monthly Report';
      break;
  }

  showNotification(`Generating ${reportName}...`, 'info');

  setTimeout(() => {
    const csvContent = generateCSVContent(filteredData);
    downloadCSV(csvContent, `${type}_report_${new Date().toISOString().split('T')[0]}.csv`);
    showNotification(`${reportName} generated successfully!`, 'success');

    // Add to report history
    addToReportHistory(type, 'CSV');
  }, 1000);
}

function filterVulnerabilitiesForReport(criteria) {
  return vulnerabilityData.vulnerabilities.filter(vuln => {
    const vulnDate = new Date(vuln.published);
    const startDate = new Date(criteria.startDate);
    const endDate = new Date(criteria.endDate);

    // Check date range
    const inDateRange = vulnDate >= startDate && vulnDate <= endDate;

    // Check severity
    let severityMatch = false;
    if (criteria.includeCritical && vuln.severity === 'Critical') severityMatch = true;
    if (criteria.includeHigh && vuln.severity === 'High') severityMatch = true;
    if (criteria.includeMedium && vuln.severity === 'Medium') severityMatch = true;
    if (criteria.includeLow && vuln.severity === 'Low') severityMatch = true;

    return inDateRange && severityMatch;
  });
}

function getReportTypeName(type) {
  const names = {
    'summary': 'Executive Summary',
    'detailed': 'Detailed Vulnerability Report',
    'vendor': 'Vendor Analysis Report',
    'trends': 'Trend Analysis Report'
  };
  return names[type] || 'Report';
}

function generateReportContent(type, data) {
  // This would generate actual PDF content in a real application
  return `Report Type: ${getReportTypeName(type)}\nVulnerabilities: ${data.length}\nGenerated: ${new Date().toLocaleString()}`;
}

