// Utility Functions
function getStatusClass(status) {
  switch(status) {
    case 'Active': return 'error';
    case 'Resolved': return 'success';
    case 'Under Investigation': return 'warning';
    default: return 'info';
  }
}

function formatDate(dateString) {
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
}

function showNotification(message, type = 'info') {
  // Create notification element
  const notification = document.createElement('div');
  notification.className = `notification notification--${type}`;
  notification.innerHTML = `
    <span>${message}</span>
    <button class="notification-close">&times;</button>
  `;

  // Add to DOM
  document.body.appendChild(notification);

  // Auto-remove after 5 seconds
  setTimeout(() => {
    if (notification.parentNode) {
      notification.parentNode.removeChild(notification);
    }
  }, 5000);

  // Add close event listener
  notification.querySelector('.notification-close').addEventListener('click', () => {
    if (notification.parentNode) {
      notification.parentNode.removeChild(notification);
    }
  });
}

function closeAlert(e) {
  const alert = e.target.closest('.alert');
  if (alert) {
    alert.style.display = 'none';
  }
}

// Real-time Updates Simulation
function simulateRealTimeUpdates() {
  // Simulate new vulnerability every 30 seconds
  setInterval(() => {
    if (Math.random() < 0.3) { // 30% chance
      simulateNewVulnerability();
    }
  }, 30000);

  // Simulate metrics updates
  setInterval(() => {
    updateMetricsWithAnimation();
  }, 15000);
}

function simulateNewVulnerability() {
  const newVuln = {
    id: Date.now().toString(),
    cve: `CVE-2024-${Math.floor(Math.random() * 9000) + 1000}`,
    title: `New ${getRandomVendor()} Security Vulnerability Detected`,
    description: "A new security vulnerability has been detected by our automated scanning system.",
    severity: getRandomSeverity(),
    cvss: (Math.random() * 4 + 6).toFixed(1), // 6.0 - 10.0
    vendor: getRandomVendor(),
    product: "Product Suite",
    version: "Latest",
    status: "Active",
    published: new Date().toISOString(),
    references: [`https://security.${getRandomVendor().toLowerCase()}.com/advisory`]
  };

  vulnerabilityData.vulnerabilities.unshift(newVuln);
  vulnerabilityData.statistics.total++;
  vulnerabilityData.statistics.active++;

  if (newVuln.severity === 'Critical') {
    vulnerabilityData.statistics.critical++;
  } else if (newVuln.severity === 'High') {
    vulnerabilityData.statistics.high++;
  } else if (newVuln.severity === 'Medium') {
    vulnerabilityData.statistics.medium++;
  }

  // Show alert for critical vulnerabilities
  if (newVuln.severity === 'Critical') {
    showCriticalAlert(newVuln);
  }

  // Update displays
  applyFilters();
  renderDashboard();

  showNotification(`New ${newVuln.severity} vulnerability detected: ${newVuln.cve}`, 'warning');
}

function showCriticalAlert(vulnerability) {
  const alertsSection = document.getElementById('alertsSection');
  const newAlert = document.createElement('div');
  newAlert.className = 'alert alert-critical fade-in';
  newAlert.innerHTML = `
    <i class="fas fa-exclamation-triangle"></i>
    <span>New Critical vulnerability detected: ${vulnerability.title}</span>
    <button class="alert-close">&times;</button>
  `;

  alertsSection.appendChild(newAlert);

  // Add close event listener
  newAlert.querySelector('.alert-close').addEventListener('click', closeAlert);

  // Auto-remove after 10 seconds
  setTimeout(() => {
    if (newAlert.parentNode) {
      newAlert.parentNode.removeChild(newAlert);
    }
  }, 10000);
}

function updateMetricsWithAnimation() {
  const metrics = document.querySelectorAll('.metric-number');
  metrics.forEach(metric => {
    metric.classList.add('fade-in');
    setTimeout(() => {
      metric.classList.remove('fade-in');
    }, 500);
  });
}

function getRandomVendor() {
  const vendors = ['Cisco', 'Microsoft', 'VMware', 'Oracle', 'Adobe', 'IBM', 'SAP'];
  return vendors[Math.floor(Math.random() * vendors.length)];
}

function getRandomSeverity() {
  const severities = ['Critical', 'High', 'Medium', 'Low'];
  const weights = [0.1, 0.3, 0.4, 0.2]; // More medium/high, fewer critical
  const random = Math.random();
  let sum = 0;

  for (let i = 0; i < weights.length; i++) {
    sum += weights[i];
    if (random < sum) {
      return severities[i];
    }
  }

  return 'Medium';
}
